pragma solidity ^0.4.22;

import "./ZoombieHelper.sol";

contract ZoombieUpgrader is ZoombieHelper {
    //可以通过冒险或喂养对zoombie进行升级

    function adventure(uint _zoombieId) external {
        // 每次冒险可以加250xp
        uint _nextTimestamp = zoombieArray[_zoombieId].nextAdventuerTimestamp;
        require(block.timestamp > _nextTimestamp,"too early");
        zoombieArray[_zoombieId].nextAdventuerTimestamp += Minutes;  // 测试:每分钟可以冒险一次，生产:每天可以冒险一次
        zoombieArray[_zoombieId].xp += xp_per_day;
    }


    function feed(uint _zoombieId) external payable{
        // 每次喂养可以加1000xp
        require(msg.value == 0.01 ether);
        uint _nextTimestamp = zoombieArray[_zoombieId].nextFeedTimestamp;
        require(block.timestamp > _nextTimestamp,"too early");
        zoombieArray[_zoombieId].nextFeedTimestamp += Minutes;  // 测试:每分钟可以喂养一次，生产:每天可以喂养一次
        zoombieArray[_zoombieId].xp += 4 * xp_per_day;
    }

    function xp_required(uint _current_level) public view returns(uint xp_to_next_level) {
        // 计算下一等级所需要的经验
        xp_to_next_level = (_current_level + 1 ) * xp_per_day;
        return xp_to_next_level;
    }

    function level_up(uint _zoombieId) external {
        uint xp_to_next_level = xp_required(zoombieArray[_zoombieId].level);
        require(zoombieArray[_zoombieId].xp >= xp_to_next_level, "not enough xp");
        zoombieArray[_zoombieId].xp -= xp_to_next_level;
        zoombieArray[_zoombieId].level += 1;
    }



}
